import React, { useState } from 'react';

const ZaalHurenFormulier = () => {
  const [formData, setFormData] = useState({
    datum: '',
    tijd: '',
    duur: '',
    grootte: '',
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Voer hier de code uit om de zaal te huren met de ingevulde gegevens
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="datum">Datum:</label>
      <input
        type="date"
        name="datum"
        id="datum"
        value={formData.datum}
        onChange={handleChange}
      />
      <br />
      <label htmlFor="tijd">Tijd:</label>
      <input
        type="time"
        name="tijd"
        id="tijd"
        value={formData.tijd}
        onChange={handleChange}
      />
      <br />
      <label htmlFor="duur">Duur (in uren):</label>
      <input
        type="number"
        name="duur"
        id="duur"
        value={formData.duur}
        onChange={handleChange}
      />
      <br />
      <label htmlFor="grootte">Grootte van het evenement:</label>
      <input
        type="number"
        name="grootte"
        id="grootte"
        value={formData.grootte}
        onChange={handleChange}
      />
      <br />
      <button type="submit">Zaal huren</button>
    </form>
  );
};

export default ZaalHurenFormulier;
